function pageReady(){$(".ms2").select2();$("#deviceSelect").select2({allowClear:!0,placeholder:"请选择",multiple:!0});getScript();getClass();getDevice()}function getClass(){ajaxPost("/Relay/Post",{opType:168},n=>{if(n.errno!=0){layer.msg(n.errmsg);return}$("#deviceType").empty().append(setOptions(n.datas,"Class"))})}function getScript(){ajaxPost("/Relay/Post",{opType:113},n=>{if(n.errno!=0){layer.msg(n.errmsg);return}$("#scriptSelect").empty().append(setOptions(n.datas,"ScriptName"))})}function getDevice(){const n=$("#scriptSelect").val();if(isStrEmptyOrUndefined(n)){layer.msg("请选择脚本");return}ajaxPost("/Relay/Post",{opType:100,scriptId:n},n=>{if(n.errno!=0){layer.msg(n.errmsg);return}$("#deviceSelect").empty().append(`<option value="0">全部</option>${setOptions(n.datas,"Code")}`)})}